export const addFav = (payload)=>{
    return{
    type : "ADD-FAVORITES",
    payload,
    }
}
export const removeFav = (payload)=>{
    return{
    type : "REMOVE-FAVORITES",
    payload,
    }
}
import { AxiosInstance } from "../../Network/AxiosInstance"

export const addMovies = () => (dispatch)=>{
   return AxiosInstance.get(`/movie/popular?api_key=addc24fa3de68655222e36f0796cadb2&page=1`)
    .then((res) => {
        dispatch({
            type: "GET_MOVIES",
            payload: res.data.results
        })
    })
    .catch((err) => console.log(err))
}
