const INITIAL_VALUE = {
    favMovies: [],
    counter: 0,
}
export default function favoritesReducer(state = INITIAL_VALUE, action) {
    switch (action.type) {
        case "ADD-FAVORITES":
            console.log(action.payload,"from")

            return {
                ...state,
                favMovies: state.favMovies.push('$action.payload'),
                counter: state.favMovies.length,

            }

        case "REMOVE-FAVORITES":
            return {
                ...state,
                favMovies: action.payload,
                counter: state.favMovies.length
            }
        default:
            return state


    }

}
