import {faMoon,faSun} from "@fortawesome/free-solid-svg-icons"
import {} from "@fortawesome/free-regular-svg-icons"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import "./Theme.css"
import { useDispatch, useSelector } from 'react-redux';
import { setTheme } from '../../Store/actions/theme';

function Theme(props) {
    const theme = useSelector(state=>state.theme.theme)
    const dispatch = useDispatch()
    return (
        <div className='theme'>
            <FontAwesomeIcon icon={fa} className='theme-icon' size='lg' onClick={()=>{theme=="light"?dispatch(setTheme("dark")):dispatch(setTheme("light"))}}/>

            <FontAwesomeIcon icon={faMoon} className='theme-icon' size='lg' onClick={()=>{theme=="light"?dispatch(setTheme("dark")):dispatch(setTheme("light"))}}/>
        </div>
    );
}

export default Theme;
