import { useParams } from "react-router-dom"
import { React, useState, useEffect } from 'react';
import axios from 'axios';
import { AxiosInstance } from "../../Network/AxiosInstance";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from "@fortawesome/free-solid-svg-icons"
import "./Movie.css"
function Movie(props) {
    const params = useParams()
    const [movie, setMovie] = useState({})
    useEffect(() => {

        AxiosInstance.get(`/${params.id}?api_key=addc24fa3de68655222e36f0796cadb2`)
            .then((res) => setMovie(res.data))
            .catch((err) => console.log(err))
    }, [])
    // let genres = movie.genres
    // let l =  {...genres.map((g,index)=> g[index].id)}
    console.log(movie)
    return (
        <div className="row movieDiv">
            <div className="col-sm-12 col-lg-6 ">
                <img src={`https://image.tmdb.org/t/p/w500/${movie.backdrop_path}`} className='movieImage'></img>

            </div>
            <div className="col-sm-12 col-lg-6">
                    <h5 className='title'>{movie.title}</h5>
                    <p className='rate'>rate: {movie.vote_average} <FontAwesomeIcon icon={faStar} /> </p>
                    <p>Release date: {movie.release_date} </p>
                    <p >{movie.overview}  </p>
                </div>

        </div>
    );
}

export default Movie;
