import { React, useState, useEffect } from 'react';
import Arrows from '../../components/Arrows/Arrows';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { AxiosInstance } from '../../Network/AxiosInstance';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar, faStarAndCrescent } from "@fortawesome/free-solid-svg-icons"
import {faStar as star} from '@fortawesome/free-regular-svg-icons'
import { useHistory, useParams } from "react-router-dom"
import "./Movies.css"
import { useDispatch, useSelector } from 'react-redux';
import Loader from '../../components/Loader/Loader';
import { setLoader } from '../../Store/actions/loader';
import { setFavorites } from '../../Store/actions/favorites';
function Movies(props) {
    const lang = useSelector((state) => state.fa.lang);

    const [movies, setMovies] = useState({})
    const isloading = useSelector(state => state.loader.isloading)
    const favorites = useSelector(state=> state.favorites.favMovies)
    const [myMoives, setMyMovies]= useState([...favorites])
    // console.log(myMoives)
    const dispatch = useDispatch()
    const params = useParams()
    const history = useHistory()
    let check = params.page
    let page = params.page ? params.page : '1'
    if (isNaN(check) && check != null) {
        history.push("/Notfound/404")
    }
    useEffect(() => {

        AxiosInstance.get(`/movie/popular?api_key=addc24fa3de68655222e36f0796cadb2&page=1`)
            .then((res) => setMovies(res.data.results))
            .catch((err) => console.log(err))
    }, [])
    useEffect(() => {

        AxiosInstance.get(`/movie/popular?api_key=addc24fa3de68655222e36f0796cadb2&page=${page}`)
            .then((res) => {
                setMovies(res.data.results)
                dispatch(setLoader(false))
            })
            .catch((err) => console.log(err))
    }, [page,myMoives])

    const handleFav = (fav)=>{
        console.log(favorites.indexOf(fav), "before")
        favorites.push(fav)
        console.log(favorites.indexOf(fav), "after")
            // console.log("my favor", myMoives)
        dispatch(setFavorites(favorites))
    }
    console.log(favorites)
    return (
        <>
            {isloading ? <Loader /> :
            <>
            <div className='row'>  
            {console.log(favorites)}          
            {Object.keys(movies).map((movie, index) => {
                    return (

                        <div className='col-sm-12 col-md-6 col-lg-4 moviesDiv' key={index}>
                          {/* {console.log(favorites.indexOf(movie))}   */}
                            {favorites.indexOf(movie)=== -1?
                            <FontAwesomeIcon className='favorSatr' icon={star} size="2x" onClick={()=>handleFav(movies[movie])}/>
                            :
                            <FontAwesomeIcon className='favorSatr' icon={faStar} size="2x" onClick={()=>handleFav(movies[movie])}/>
                            }
                            <img src={`https://image.tmdb.org/t/p/w500/${movies[movie].backdrop_path}`} className='movieImage'></img>
                            <h5 className='title'>{movies[movie].title}</h5>
                            <p className='rate'>rate: {movies[movie].vote_average} <FontAwesomeIcon icon={faStar} /> </p>
                            <Link key={index} to={`/details/${movies[movie].id}`}>
                                <button type="submit" className="btn btn-success SignupButton" name="button" >Show details</button>
                            </Link>
                        </div>
                    )
                })} 
            </div>
            <Arrows history={history} page={page} />
          </>
          }
        </>
    );
}

export default Movies;
