import {useParams} from "react-router-dom"
import { React, useState, useEffect } from 'react';
import axios from 'axios';
import { AxiosInstance } from "../../Network/AxiosInstance";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar} from "@fortawesome/free-solid-svg-icons"
function Movie(props) {
    const params = useParams()
    const [movie, setMovie] = useState({})
    useEffect(() => {

        AxiosInstance.get(`/${params.id}?api_key=addc24fa3de68655222e36f0796cadb2`)
            .then((res) => setMovie(res.data))
            .catch((err) => console.log(err))
    }, [])
    console.log(movie)
    return (
        <div className="row">
            <div className="col-6">
            <img src={`https://image.tmdb.org/t/p/w500/${movie.backdrop_path}`} className='movieImage'></img>
            <div className="col-6">
            <h5 className='title'>{movie.title}</h5>
            <p className='rate'>rate: {movie.overview} <FontAwesomeIcon icon={faStar} /> </p>
            </div>

            </div>
        </div>
    );
}

export default Movie;
