import { React, useState, useEffect } from 'react';
import Arrows from '../../components/Arrows/Arrows';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { AxiosInstance } from '../../Network/AxiosInstance';
import {useHistory, useParams} from "react-router-dom"

function Movies(props) {
    const history = useHistory()

    const [movies, setMovies] = useState({})
    const params = useParams()
    console.log(params)
    useEffect(() => {

        AxiosInstance.get(`/popular?api_key=addc24fa3de68655222e36f0796cadb2&page=${params.page}`)
            .then((res) => setMovies(res.data.results))
            .catch((err) => console.log(err))
    }, [])

    return (
        <>
            {Object.keys(movies).map((movie, index) => {
                return(
                    <Link key={index} to={`/details/${movies[movie].id}`}>
                        <img src={`https://image.tmdb.org/t/p/w500/${movies[movie].backdrop_path}`} ></img>
                        <p>{movies[movie].title}</p>
                    </Link>
                )
            })}
            <Arrows history={history}/>
        </>
    );
}

export default Movies;
