const INITIAL_VALUE = {
    favMovies: [],
    counter: 0,
}
export default function favoritesReducer(state = INITIAL_VALUE, action) {
    switch (action.type) {
        case "ADD-FAVORITES":
            //console.log(action.payload,"from")

            return {
                ...state,
                favMovies: state.favMovies.concat(action.payload),
                counter: state.favMovies.length+1,

            }

        case "REMOVE-FAVORITES":
            const index = state.favMovies.map((movie)=> movie.id).indexOf(action.payload.id)
            fav = state.favMovies.filter((item, id)=>id !== index )
            return {
                ...state,
                favMovies: fav,
                counter: fav.length+1
            }
        default:
            return state


    }

}
