import { React, useState, useEffect } from 'react';
import Arrows from '../../components/Arrows/Arrows';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { AxiosInstance } from '../../Network/AxiosInstance';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faStar } from "@fortawesome/free-solid-svg-icons"
import { useHistory, useParams } from "react-router-dom"
import "./Movies.css"
function Movies(props) {
    const history = useHistory()

    const [movies, setMovies] = useState({})
    const params = useParams()
    let check = params.page
    console.log(check)
    let page = params.page ? params.page : '1'
    if (isNaN)
    useEffect(() => {

        AxiosInstance.get(`/popular?api_key=addc24fa3de68655222e36f0796cadb2&page=1`)
            .then((res) => setMovies(res.data.results))
            .catch((err) => console.log(err))
    }, [])
    useEffect(() => {

        AxiosInstance.get(`/popular?api_key=addc24fa3de68655222e36f0796cadb2&page=${page}`)
            .then((res) => setMovies(res.data.results))
            .catch((err) => console.log(err))
    }, [page])

    return (
        <>
            <div className='row'>
                {Object.keys(movies).map((movie, index) => {
                    return (

                        <div className='col-sm-12 col-md-6 col-lg-4 movieDiv'>
                            <img src={`https://image.tmdb.org/t/p/w500/${movies[movie].backdrop_path}`} className='movieImage'></img>
                            <h5 className='title'>{movies[movie].title}</h5>
                            <p className='rate'>rate: {movies[movie].vote_average} <FontAwesomeIcon icon={faStar} /> </p>
                            <Link key={index} to={`/details/${movies[movie].id}`}>
                                <button type="submit" className="btn btn-success SignupButton" name="button" >Show details</button>
                            </Link>
                        </div>
                    )
                })}
            </div>
            <Arrows history={history} page={page} />
        </>
    );
}

export default Movies;
