const INITIAL_VALUE = {
    favmovies: [],
    counter: 0,
}
export default function favoritesReducer(state = INITIAL_VALUE, action){
    switch(action.type){
        case "FAVORITES":
            return{
                ...state,
                favorites: action.payload,
                conunter: action.payload.length
            }
        default:
            return state
    }
}
