import { counter } from "@fortawesome/fontawesome-svg-core"

const INITIAL_VALUE = {
    favMovies: [1],
    counter: 0,
}
export default function favoritesReducer(state = INITIAL_VALUE, action) {
    switch (action.type) {
        case "ADD-FAVORITE":
            console.log(action.payload.length, "favvvv")
            return {
                ...state,
                favMovies: action.payload,
                conunter: counter++
            }
        case "REMOVE-FAVORITE":
            console.log(action.payload.length, "favvvv")
            return {
                ...state,
                favMovies: action.payload,
                conunter: counter--
            }
        default:
            return state
    }
}
